    IF @cmdshell = 0
        BEGIN
            EXEC sp_configure 'xp_cmdshell', 0;
            RECONFIGURE WITH OVERRIDE;
        END;

    IF @showadvanced = 0
        BEGIN
            EXEC sp_configure 'show advanced options', 0;
            RECONFIGURE WITH OVERRIDE;
        END;
	DECLARE @Print NVARCHAR(4000);
	SET @Print = 'Go tack your file from here - "' + @Filename + '"';
	PRINT @Print;
END;

BEGIN SET NOCOUNT ON;
SELECT H.database_name,H.replica_server_name,H.ag_name, CONCAT('Database - ',H.database_name,' on the ',case H.is_local when 1 then 'local server' when 0 then 'remote(' + H.replica_server_name + ')' else '' end, ' that in group - ',H.ag_name,' are ',H.synchronization_state_desc,' and its ',H.synchronization_health_desc) [Message] FROM Client.HADR H WHERE H.Guid = @guid AND (H.synchronization_state_desc != 'SYNCHRONIZED' OR H.synchronization_health_desc != 'HEALTHY') UNION ALL SELECT AOL.database_name,AOL.PrimaryServer,AOL.AlwaysOnGroup, CONCAT('Database - ',AOL.database_name,' on the server ' ,AOL.PrimaryServer, ' that in group - ',AOL.AlwaysOnGroup,' have high Latency - ',AOL.lag_in_milliseconds/1000,' secondes.') [Message] FROM Client.AlwaysOnLatency AOL WHERE AOL.Guid = @guid AND AOL.lag_in_milliseconds > 600000 UNION ALL SELECT NULL,IIF(LEN(M.[Replica])> 15,NULL,M.[Replica]) [Replica],IIF(LEN(M.Msg)> 15,NULL,M.Msg) [AlwaysOnGroup],H.[Msg] FROM [Client].[HADRStatus] H CROSS APPLY (SELECT Utility.ufn_Util_clr_RegexReplace(H.[Msg],'(Availability Group\- \[([\w\W]*)\][\w\W]*)','$2',0) [Msg], Utility.ufn_Util_clr_RegexReplace(H.[Msg],'(Replica name \- \[([\w\W]*)\][\w\W]*)','$2',0) [Replica])M WHERE [guid] = @guid
END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT CONCAT(B.FindingsGroup,'-',B.Finding,'-',IIF(B.DatabaseName='','',CONCAT('(',B.DatabaseName,')')),B.Details) [Message] FROM Client.Blitz B WHERE B.guid = @Guid AND B.Finding NOT IN ('High VLF Count') AND B.FindingsGroup NOT IN ('Wait Stats','Rundate','Server Info','Informational','Licensing','Non-Default Server Config')
END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT ROW_NUMBER() OVER (ORDER BY V.VolumeName) ID, V.VolumeName , V.BlockSize/1024 [BlockSize] INTO #Block FROM Client.Volumes V WHERE V.guid = @Guid --AND V.BlockSize != 65536;
SELECT V1.ID ID1, V1.VolumeName VolumeName1, V1.[BlockSize] BlockSize1, IIF(V1.[BlockSize] = 64,'Disk Block in propre size','Change Block size to 64K')[Note1], IIF(V1.[BlockSize] = 64,'Green','Red')[Color1], V2.ID ID2, V2.VolumeName VolumeName2, V2.[BlockSize] BlockSize2, IIF(V2.[BlockSize] = 64,'Disk Block in propre size','Change Block size to 64K')[Note2], IIF(V2.[BlockSize] = 64,'Green','Red')[Color2], V3.ID ID3, V3.VolumeName VolumeName3, V3.[BlockSize] BlockSize3, IIF(V3.[BlockSize] = 64,'Disk Block in propre size','Change Block size to 64K')[Note3], IIF(V3.[BlockSize] = 64,'Green','Red')[Color3] FROM #Block V1 LEFT JOIN #Block V2 ON V1.ID + 1 = V2.ID AND V2.ID % 3 = 2 LEFT JOIN #Block V3 ON V1.ID + 2 = V3.ID AND V3.ID % 3 = 0 WHERE V1.ID % 3 = 1;
END

BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	DECLARE @ProcessorName NVARCHAR(MAX)

	SELECT	@ProcessorName = REPLACE(REPLACE([Utility].[PatternReplace](REPLACE(MS.ProcessorName,'(R)',''),'  ',' '),'@','%40'),' ','+')
	FROM	Client.MachineSettings MS
	WHERE	MS.guid = @Guid;

	SELECT CONCAT('http://www.cpubenchmark.net/cpu.php?cpu=',@ProcessorName) [Link]--http://www.cpubenchmark.net/cpu.php?cpu=Intel+Xeon+X5660+%40+2.80GHz
END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT MS.ProcessorName, --TRY_CONVERT(FLOAT,Utility.ufn_Util_clr_RegexReplace('Intel(R) Xeon(R) CPU E5-2643 v2 @ 3.50GHz','[\W\w]*@\s+([\d]*\.[\d]*)GHz','$1',0))[CoreRate], --TRY_CONVERT(FLOAT,Utility.ufn_Util_clr_RegexReplace('Intel(R) Xeon(R) CPU E5-2643 v2 @ 3.50GHz','[\W\w]*@\s+([\d]*\.[\d]*)GHz','$1',0))[CurrentCoreRate], ROUND(MS.MaxClockSpeed/1000.0,2) [CoreRate], ROUND(MS.CurrentClockSpeed/1000.0,2) [CurrentCoreRate], TRY_CONVERT(FLOAT,4.0) OfficalMaxCoreRate, TRY_CONVERT(FLOAT,1.5) OfficalMinCoreRate FROM Client.MachineSettings MS WHERE MS.guid = @Guid;
END

BEGIN SET NOCOUNT ON;
DECLARE @Img VARBINARY(MAX); DECLARE @Collation sysname; DECLARE @compatibility_level INT; DECLARE @Ver NVARCHAR(128);
SELECT @Collation = Collation, @Ver = ISNULL(ProductVersion,Utility.ufn_Util_clr_RegexReplace(Version,'([\W\w]*)\- ([\d]+\.[\d]+\.[\d]+\.[\d]+)[\W\w]*','$2',0)) FROM Client.[MachineSettings] WHERE guid = @guid; SELECT @compatibility_level = TRY_CONVERT(INT,PARSENAME(CONVERT(VARCHAR(32), @Ver), 4)+ LEFT(PARSENAME(CONVERT(VARCHAR(32), @Ver), 3),1)) SELECT @Img = img FROM [Configuration].[Images] WHERE ID = 16;--DB Image DECLARE @Ignore TABLE([Type] sysname); IF EXISTS(SELECT TOP 1 1 FROM Client.Software S WHERE S.Status = 1 AND S.guid = @guid) INSERT @Ignore VALUES ('Auto Create Statistics'),('Auto Updtae Statistics');
DECLARE @NumberOfDataFiles INT; DECLARE @NumberOfCPU INT;
SELECT @NumberOfDataFiles = COUNT(1) FROM [Client].[DatabaseFiles] df WHERE df.guid = @Guid AND df.Database_Name = 'tempdb' AND df.File_Type = 'Data';
SELECT @NumberOfCPU = SP.logicalCPU FROM Client.ServerProporties SP WHERE SP.guid = @Guid;
SELECT D.database_id,D.name,ISNULL(OA.img,@Img)img INTO #Img FROM Client.Databases D LEFT JOIN [Configuration].[Database] ED ON ED.DatabaseName = D.name OUTER APPLY (SELECT TOP 1 i.img FROM [Configuration].[Images] i WHERE (D.database_id BETWEEN 1 AND 4 AND i.ID = 11) OR (D.IsBizTalk = 1 AND i.ID = 12) OR (D.IsCRMDynamics = 1 AND i.ID = 13) OR (D.IsSharePoint = 1 AND i.ID = 15) OR (D.IsSAP = 1 AND i.ID = 14) OR (D.IsTFS = 1 AND i.ID = 22) OR (D.name LIKE '%ReportServer%' AND i.ID = 18) OR (D.name = 'SSIS' AND i.ID = 19) OR (ED.TypeID = 2 AND i.ID = 20) )OA WHERE D.guid = @guid;
SELECT DISTINCT [Utility].[ufn_CapitalizeFirstLetter](DP.Type) [Type] , DP.DatabaseName , i.img [Image], DP.Note, D.database_id FROM Client.DatabaseProperties DP INNER JOIN Client.Databases D ON D.guid = DP.guid AND D.name = DP.DatabaseName INNER JOIN #Img i ON i.database_id = D.database_id WHERE DP.guid = @guid AND DP.[Type] NOT IN (SELECT [Type] FROM @Ignore) UNION ALL
SELECT 'Collation',D.name, i.img [Image],'Databases have diffarent Collation from the server configuration', D.database_id FROM Client.Databases D INNER JOIN #Img i ON i.database_id = D.database_id WHERE D.guid = @guid AND D.collation_name != @Collation AND D.name NOT IN ('ReportServer','ReportServerTempdb') UNION ALL SELECT 'Auto Close',D.name, i.img [Image],'Databases have is_auto_close proporty on. this is not recommended', D.database_id FROM Client.Databases D INNER JOIN #Img i ON i.database_id = D.database_id WHERE D.guid = @guid AND D.is_auto_close_on = 1 UNION ALL SELECT 'Auto Shrink',D.name, i.img [Image],'Databases have is_auto_shrink proporty on. this is not recommended', D.database_id FROM Client.Databases D INNER JOIN #Img i ON i.database_id = D.database_id WHERE D.guid = @guid AND D.is_auto_shrink_on = 1 UNION ALL SELECT 'Compatibility Level',D.name, i.img [Image],'Databases have compatibility_level(' + TRY_CONVERT(VARCHAR(5),D.compatibility_level) + ') lower then the server(' + CONVERT(VARCHAR(5),@compatibility_level) + '). this is not recommanded', D.database_id FROM Client.Databases D INNER JOIN #Img i ON i.database_id = D.database_id WHERE D.guid = @guid AND D.compatibility_level != @compatibility_level UNION ALL SELECT 'Log File',DF.Database_Name, i.img [Image],CONCAT('Log file size [(Log) ' , CONVERT(NVARCHAR(1000),L.Size) , 'MB / ' , CONVERT(NVARCHAR(1000),SUM(DF.Total_Size)) , 'MB (Data)] is ' , CONVERT(INT,ROUND((L.Size / (SUM(DF.Total_Size)*1.0) * 100),0)) , '% then Database size'), DF.database_id FROM Client.DatabaseFiles DF CROSS APPLY (SELECT SUM(iDF.Total_Size) Size FROM Client.DatabaseFiles iDF WHERE iDF.guid = @guid AND iDF.File_Type = 'Log' AND iDF.Database_Name = DF.Database_Name)L INNER JOIN #Img i ON i.database_id = DF.database_id WHERE DF.guid = @guid AND DF.File_Type = 'Data' GROUP BY DF.Database_Name,L.Size,i.img, DF.database_id HAVING L.Size / SUM(DF.Total_Size) > 0.3 OR L.Size > SUM(DF.Total_Size) UNION ALL SELECT 'Database File',DF.Database_Name, i.img [Image],CONCAT('Data file size [', CONVERT(NVARCHAR(1000),SUM(DF.Total_Size)) , 'MB (Data)] is large. Check for more reasons.'), DF.database_id FROM Client.DatabaseFiles DF INNER JOIN #Img i ON i.database_id = DF.database_id WHERE DF.guid = @guid AND DF.File_Type = 'Data' AND DF.database_id = 4 --msdb GROUP BY DF.Database_Name,DF.database_id,i.img HAVING SUM(DF.Total_Size) > 2000 UNION ALL SELECT 'Database File',DF.Database_Name, im.img [Image],CONCAT('Data file is locate with ', Utility.ufn_Util_clr_Conc(i.File_Type) , ' file. Please choose diffrent drive for etch file type.'), DF.database_id FROM Client.DatabaseFiles DF CROSS APPLY (SELECT DISTINCT LEFT(iDF.Physical_Name,3) Volume,iDF.File_Type FROM Client.DatabaseFiles iDF WHERE iDF.guid = @guid AND iDF.file_id != 1)i INNER JOIN #Img im ON im.database_id = DF.database_id WHERE DF.guid = @guid AND DF.file_id = 1 AND LEFT(DF.Physical_Name,3) = i.Volume AND DF.database_id > 4 GROUP BY DF.Database_Name,DF.database_id,im.img UNION ALL SELECT 'Database File','tempdb',im.img [Image],CONCAT('Add ',COUNT(1) - 1,' more data files to database.' ),2 FROM (SELECT v.number n FROM master..spt_values v WHERE v.type = 'p' AND v.number <= CASE WHEN @NumberOfDataFiles >= @NumberOfCPU THEN @NumberOfDataFiles WHEN @NumberOfDataFiles >= 8 THEN @NumberOfDataFiles WHEN @NumberOfDataFiles <= @NumberOfCPU AND @NumberOfCPU <= 4 THEN @NumberOfCPU WHEN @NumberOfDataFiles <= @NumberOfCPU AND @NumberOfCPU > 4 AND @NumberOfCPU < 8 THEN @NumberOfCPU WHEN @NumberOfDataFiles <= @NumberOfCPU AND @NumberOfCPU >= 8 THEN 8 ELSE @NumberOfDataFiles END )Num INNER JOIN #Img im ON im.database_id = 2 LEFT JOIN (SELECT *,ROW_NUMBER() OVER (ORDER BY df.file_id) RN FROM [Client].[DatabaseFiles] df WHERE df.guid = @Guid AND df.Database_Name = 'tempdb' AND df.File_Type = 'Data')RDF ON Num.n = RDF.RN
CROSS JOIN(SELECT TOP 1 Sdf.Total_Size,COUNT(1) Num FROM [Client].[DatabaseFiles] Sdf WHERE Sdf.guid = @Guid AND Sdf.Database_Name = 'tempdb' AND Sdf.File_Type = 'Data' GROUP BY Sdf.Total_Size ORDER BY COUNT(1) DESC,Sdf.Total_Size DESC)S WHERE RDF.[File_Name] IS NULL GROUP BY im.img HAVING COUNT(1) > 0 UNION ALL SELECT 'Database File',DF.Database_Name, im.img [Image],'TempDB files are located with other database files. Please choose diffrent drive for TempDB files.', DF.database_id FROM Client.DatabaseFiles DF CROSS APPLY (SELECT DISTINCT LEFT(iDF.Physical_Name,3) Volume,iDF.File_Type FROM Client.DatabaseFiles iDF WHERE iDF.guid = @guid AND iDF.database_id != 2 AND LEFT(DF.Physical_Name,3) = LEFT(iDF.Physical_Name,3))i INNER JOIN #Img im ON im.database_id = DF.database_id WHERE DF.guid = @guid AND DF.file_id = 1 AND LEFT(DF.Physical_Name,3) = i.Volume AND DF.database_id = 2 GROUP BY DF.Database_Name,DF.database_id,im.img ORDER BY database_id ASC,2 ASC; END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT TOP 10 DF.database_id,DF.Database_Name,SUM(DF.Total_Size)/1024[DataSize],LDF.Size [LogSize],LDF.Size + (SUM(DF.Total_Size)/1024) [Total] FROM Client.DatabaseFiles DF CROSS APPLY (SELECT SUM(LDF.Total_Size)/1024[Size] FROM Client.DatabaseFiles LDF WHERE LDF.database_id = DF.database_id AND LDF.guid = DF.guid AND LDF.File_Type = 'Log' GROUP BY LDF.database_id, LDF.guid, LDF.File_Type)LDF WHERE DF.database_id IS NOT NULL AND DF.guid = @Guid AND DF.File_Type = 'Data' GROUP BY DF.database_id,DF.Database_Name,LDF.Size ORDER BY SUM(DF.Total_Size) DESC;
END

BEGIN SET NOCOUNT ON;
SELECT 'Report Number'[Name],CONVERT(NVARCHAR(MAX),@guid)[Value],NULL [Note] UNION ALL SELECT 'Client name',RMD.ClientName , NULL FROM Client.ReportMetaData RMD WHERE RMD.ReportGUID = @guid UNION ALL SELECT 'Date of collection',CONVERT(NVARCHAR(MAX),RMD.RunDate,103) , NULL FROM Client.ReportMetaData RMD WHERE RMD.ReportGUID = @guid UNION ALL SELECT 'Client Analysis Version',RMD.ClientVersion , IIF(RMD.ClientVersion < '1.00','Alfa Version',NULL) FROM Client.ReportMetaData RMD WHERE RMD.ReportGUID = @guid UNION ALL SELECT TOP 1 'Server Analysis Version' [App],V.FullVersion, IIF(V.FullVersion < '1.00','Alfa Version',NULL) FROM [Configuration].Version V WHERE V.Platform = 'Server' UNION ALL SELECT TOP 1 'Report Version' [App],V.FullVersion, IIF(V.FullVersion < '1.00','Alfa Version',NULL) FROM [Configuration].Version V WHERE V.Platform = 'Report' ORDER BY 2 DESC;
END

BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	SELECT	TOP 10 Utility.ufn_Util_clr_Conc(DISTINCT CheckType) [Type],database_name,LEFT(query_text,100)[Query],AVG(AvgDuration)AvgDuration,AVG(AvgScore)[AvgScore],SUM(execution_count)[ExecutionCount],MAX(last_execution_time)[LastExecutionTime]
	FROM	[Client].[HeavyQueries]
	WHERE	guid = @Guid
	GROUP BY LEFT(query_text,100),database_name
	ORDER BY 6 DESC,7 DESC;

END

BEGIN
	SET NOCOUNT ON;
	SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;

	IF @Property = 'AlwaysON'
	BEGIN
	    SELECT TOP 1 HS.AlwaysOn [IsPropertyOn] FROM Client.HADRServices HS WHERE HS.Guid = @Guid;
		RETURN;
	END
	IF @Property = 'Mirror'
	BEGIN
	    SELECT TOP 1 HS.Mirror [IsPropertyOn] FROM Client.HADRServices HS WHERE HS.Guid = @Guid;
		RETURN;
	END
	IF @Property = 'LogShipping'
	BEGIN
	    SELECT TOP 1 HS.LogShipping [IsPropertyOn] FROM Client.HADRServices HS WHERE HS.Guid = @Guid;
		RETURN;
	END
	IF @Property = 'Replication'
	BEGIN
	    SELECT TOP 1 HS.[Replication] [IsPropertyOn] FROM Client.HADRServices HS WHERE HS.Guid = @Guid;
		RETURN;
	END
END

