END

BEGIN SET NOCOUNT ON;
SELECT M.DatabaseName , CASE M.Role WHEN 1 THEN 'PRINCIPAL' WHEN 2 THEN 'MIRROR' END [Role], CASE M.MirroringState WHEN 0 THEN 'SUSPENDED' WHEN 1 THEN 'DISCONNECTED' WHEN 2 THEN 'SYNCHRONIZING' WHEN 3 THEN 'PENDING FAILOVER' WHEN 4 THEN 'SYNCHRONIZED' END MirroringState, CASE M.WitnessStatus WHEN 0 THEN 'UNKNOWN' WHEN 1 THEN 'CONNECTED' WHEN 2 THEN 'DISCONNECTED' END WitnessStatus, M.LogGeneratRate , M.UnsentLog , M.SentRate , M.UnrestoredLog , M.RecoveryRate , M.TransactionDelay , M.TransactionPerSec , M.AverageDelay , M.TimeRecorded , M.TimeBehind , M.LocalTime FROM Client.Mirror M WHERE M.guid = @guid
END

BEGIN SET NOCOUNT ON;
DECLARE @Ver NVARCHAR(128)
SELECT @Ver = CAST(ISNULL(ProductVersion,Utility.ufn_Util_clr_RegexReplace(Version,'Microsoft SQL Server [\d]+ \- ([\d]+\.[\d]+\.[\d]+\.[\d]+)[\W\w]*','$1',0))AS NVARCHAR(128)) FROM Client.MachineSettings MS WHERE @guid = MS.guid;
IF EXISTS(SELECT TOP 1 1 FROM Configuration.SQLServerBuild SSB INNER JOIN ( SELECT TOP 1 * FROM Configuration.SQLServerBuild SSB WHERE PARSENAME(CONVERT(VARCHAR(32), @Ver), 4) = SSB.Major AND CONVERT(INT,PARSENAME(CONVERT(VARCHAR(32), @Ver), 3)) = CONVERT(INT,SSB.Minor) AND PARSENAME(CONVERT(varchar(32), @Ver), 2) = SSB.VersionBuild AND PARSENAME(CONVERT(VARCHAR(32), @Ver), 1) = ISNULL(SSB.Revision,PARSENAME(CONVERT(VARCHAR(32), @Ver), 1)) )T ON T.Major = SSB.Major AND t.Minor = SSB.Minor OUTER APPLY (SELECT TOP 1 T.Build , T.Description , T.ReleaseDate , T.ShortName , T.Major,T.Minor,T.VersionBuild,T.Revision FROM ( SELECT SSB.Build,SSB.Description,SSB.ReleaseDate, CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END [ShortName], ROW_NUMBER() OVER (PARTITION BY CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END ORDER BY SSB.Build DESC) RN,SSB.Major,SSB.Minor,SSB.VersionBuild,SSB.Revision FROM Configuration.SQLServerBuild SSB WHERE PARSENAME(CONVERT(VARCHAR(32), @Ver), 4) = SSB.Major AND CONVERT(INT,PARSENAME(CONVERT(VARCHAR(32), @Ver), 3)) = CONVERT(INT,SSB.Minor) AND SSB.Build > @Ver)T WHERE T.RN = 1 AND T.ShortName = 'SP')SP WHERE T.Build < SSB.Build AND ISNULL(SP.Build,T.Build) <= SSB.Build AND SSB.Build NOT IN ('11.00.9120','11.00.9000')) BEGIN SELECT SSB.Build,SSB.Description,SSB.ReleaseDate, CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END [ShortName] FROM Configuration.SQLServerBuild SSB INNER JOIN ( SELECT TOP 1 * FROM Configuration.SQLServerBuild SSB WHERE PARSENAME(CONVERT(VARCHAR(32), @Ver), 4) = SSB.Major AND CONVERT(INT,PARSENAME(CONVERT(VARCHAR(32), @Ver), 3)) = CONVERT(INT,SSB.Minor) AND PARSENAME(CONVERT(varchar(32), @Ver), 2) = SSB.VersionBuild AND PARSENAME(CONVERT(VARCHAR(32), @Ver), 1) = ISNULL(SSB.Revision,PARSENAME(CONVERT(VARCHAR(32), @Ver), 1)) )T ON T.Major = SSB.Major AND t.Minor = SSB.Minor OUTER APPLY (SELECT TOP 1 T.Build , T.Description , T.ReleaseDate , T.ShortName , T.Major,T.Minor,T.VersionBuild,T.Revision FROM ( SELECT SSB.Build,SSB.Description,SSB.ReleaseDate, CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END [ShortName], ROW_NUMBER() OVER (PARTITION BY CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END ORDER BY SSB.Build DESC) RN,SSB.Major,SSB.Minor,SSB.VersionBuild,SSB.Revision FROM Configuration.SQLServerBuild SSB WHERE PARSENAME(CONVERT(VARCHAR(32), @Ver), 4) = SSB.Major AND CONVERT(INT,PARSENAME(CONVERT(VARCHAR(32), @Ver), 3)) = CONVERT(INT,SSB.Minor) AND SSB.Build > @Ver)T WHERE T.RN = 1 AND T.ShortName = 'SP')SP WHERE T.Build < SSB.Build AND ISNULL(SP.Build,T.Build) <= SSB.Build AND SSB.Build NOT IN ('11.00.9120','11.00.9000') ORDER BY SSB.Build DESC; END ELSE BEGIN SELECT TOP 1 SSB.Build,'The server have the last update version installed',SSB.ReleaseDate, CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END [ShortName] FROM Configuration.SQLServerBuild SSB INNER JOIN ( SELECT TOP 1 * FROM Configuration.SQLServerBuild SSB WHERE PARSENAME(CONVERT(VARCHAR(32), @Ver), 4) = SSB.Major AND CONVERT(INT,PARSENAME(CONVERT(VARCHAR(32), @Ver), 3)) = CONVERT(INT,SSB.Minor) AND PARSENAME(CONVERT(varchar(32), @Ver), 2) = SSB.VersionBuild AND PARSENAME(CONVERT(VARCHAR(32), @Ver), 1) = ISNULL(SSB.Revision,PARSENAME(CONVERT(VARCHAR(32), @Ver), 1)) )T ON T.Major = SSB.Major AND t.Minor = SSB.Minor WHERE SSB.Build NOT IN ('11.00.9120','11.00.9000') ORDER BY SSB.Build DESC; END END

BEGIN SET NOCOUNT ON;
SELECT R.Messages,ISNULL(R.Link,RL.Link)[Link],IIF(ISNULL(R.Link,RL.Link) IS NULL,'Black','Blue')[Color] FROM [Client].[Replications] R LEFT JOIN [Configuration].[ReplicationLink] RL ON RL.ID = R.ID AND R.Link IS NULL WHERE R.guid = @guid ORDER BY R.ID ASC
END

BEGIN SET NOCOUNT ON;
DECLARE @hyperthreadingRatio BIT; DECLARE @logicalCPUs INT; DECLARE @HTEnabled INT; DECLARE @physicalCPU INT; DECLARE @SOCKET INT; DECLARE @logicalCPUPerNuma INT; DECLARE @NoOfNUMA INT; DECLARE @MaxDOP INT; DECLARE @CurentMaxDOP INT; DECLARE @PhysicalMemory INT ; DECLARE @OSBit INT;
-- find NO OF NUMA Nodes SELECT @NoOfNUMA = COUNT(DISTINCT OS.parent_node_id) FROM Client.os_schedulers OS WHERE OS.guid = @Guid AND OS.status = 'VISIBLE ONLINE' AND OS.parent_node_id < 64;
SELECT @logicalCPUs = SP.logicalCPU -- [Logical CPU Count] ,@hyperthreadingRatio = hyperthread_ratio -- [Hyperthread Ratio] ,@physicalCPU = SP.CPU_Core -- [Physical CPU Count] ,@HTEnabled = CASE WHEN SP.logicalCPU > hyperthread_ratio THEN 1 ELSE 0 END -- HTEnabled ,@OSBit = SP.OS_bit FROM [Client].[ServerProporties] SP WHERE SP.guid = @Guid;
SELECT @logicalCPUPerNuma = COUNT(OS.parent_node_id) -- [NumberOfLogicalProcessorsPerNuma] FROM Client.os_schedulers OS WHERE OS.guid = @Guid AND OS.[status] = 'VISIBLE ONLINE' AND OS.parent_node_id < 64 GROUP BY OS.parent_node_id;
IF NOT EXISTS (SELECT TOP 1 1 FROM Client.Software S WHERE S.guid = @guid AND S.Status = 1) BEGIN IF @NoOfNUMA = 1 BEGIN IF @logicalCPUPerNuma > 8 BEGIN IF EXISTS(SELECT TOP 1 1 FROM sys.dm_os_sys_info OS WHERE OS.virtual_machine_type = 1) --Virtual BEGIN SET @NoOfNUMA = 2; --PRINT 'On this VM "Hot Plug" is enabled'; SET @MaxDOP = 8; END END ELSE BEGIN SET @MaxDOP = @logicalCPUPerNuma; END END ELSE BEGIN IF @logicalCPUPerNuma > 8 BEGIN SET @MaxDOP = 8; END ELSE BEGIN SET @MaxDOP = @logicalCPUPerNuma; END END END ELSE BEGIN SET @MaxDOP = 1; END
--https://support.microsoft.com/en-us/kb/2806535 --https://www.brentozar.com/archive/2014/11/many-cpus-parallel-query-using-sql-server/ --https://www.littlekendra.com/2016/07/14/max-degree-of-parallelism-cost-threshold-for-parallelism/ --https://blogs.msdn.microsoft.com/sqlsakthi/p/maxdop-calculator-sqlserver/
DECLARE @Ver NVARCHAR(128) SELECT TOP 1 @PhysicalMemory = CONVERT(INT,ROUND(TRY_CONVERT(BIGINT,LEFT(ISNULL(PhysicalMemory, ''), CHARINDEX(' ', PhysicalMemory) - 1)),0)), @Ver = LEFT(PARSENAME(CONVERT(VARCHAR(32), ISNULL(ProductVersion,Utility.ufn_Util_clr_RegexReplace(Version,'([\W\w]*)\- ([\d]+\.[\d]+\.[\d]+\.[\d]+)[\W\w]*','$2',0))), 4) + PARSENAME(CONVERT(VARCHAR(32), ISNULL(ProductVersion,Utility.ufn_Util_clr_RegexReplace(Version,'([\W\w]*)\- ([\d]+\.[\d]+\.[\d]+\.[\d]+)[\W\w]*','$2',0))), 3),3) FROM [Client].[MachineSettings] WHERE guid = @guid;
SELECT @PhysicalMemory = CASE WHEN @PhysicalMemory <= 8000 THEN 6000 ELSE @PhysicalMemory * 0.9 END --5120 0.9 DECLARE @Ignore TABLE(name sysname NOT NULL); INSERT @Ignore VALUES ( 'max worker threads'),('show advanced options'),('cost threshold for parallelism'),('max degree of parallelism');
IF EXISTS(SELECT TOP 1 1 FROM Client.MachineSettings MS WHERE MS.Edition LIKE '%Express Edition%' AND MS.guid = @guid) BEGIN INSERT @Ignore VALUES ('Agent XPs'); END SELECT cSP.[name] , cSP.[value] value , c.[Default] , CASE WHEN cSP.[name] = 'max server memory (MB)' THEN @PhysicalMemory WHEN cSP.[name] = 'min server memory (MB)' THEN CONVERT(INT,@PhysicalMemory * 0.8) ELSE c.BestPractice END BestPractice, c.BedPractice BadPractice, c.[Link], IIF(c.[Link] IS NOT NULL,'Blue','Black')[Color] FROM [Client].[spconfigure] cSP INNER JOIN [Configuration].[sp_configurations] c ON cSP.name LIKE c.Name + '%' AND ( ISNULL(c.BestPractice,c.[Default]) != c.[Default] --OR c.BedPractice IS NOT NULL ) AND ( cSP.value != ISNULL(c.BestPractice,cSP.value) OR cSP.value = ISNULL(c.BedPractice,-1) ) WHERE cSP.guid = @guid AND cSP.name NOT IN ( 'max worker threads','show advanced options','cost threshold for parallelism','max degree of parallelism') UNION ALL SELECT 'min = max server memory (MB)' [name] , cSP.[value] value , 0 [Default] , CONVERT(INT,@PhysicalMemory * 0.8) BestPractice, cSP.[value], NULL [Link], 'Black' [Color] FROM [Client].[spconfigure] cSP CROSS APPLY (SELECT value FROM [Client].[spconfigure] IC WHERE IC.guid = @guid AND IC.name = 'max server memory (MB)')MI WHERE cSP.guid = @guid AND cSP.name = 'min server memory (MB)' AND MI.value = cSP.value UNION ALL SELECT cSP.[name] , cSP.[value] , c.[Default] , c.BestPractice, c.BedPractice BadPractice, c.[Link], IIF(c.[Link] IS NOT NULL,'Blue','Black')[Color] FROM [Client].[spconfigure] cSP INNER JOIN [Configuration].[sp_configurations] c ON cSP.name LIKE c.Name + '%' AND ( ISNULL(c.BestPractice,c.[Default]) != c.[Default] --OR c.BedPractice IS NOT NULL ) AND ( cSP.value != ISNULL(c.BestPractice,cSP.value) OR cSP.value = ISNULL(c.BedPractice,-1) ) WHERE cSP.guid = @guid AND cSP.name = 'cost threshold for parallelism' AND cSP.value <= 5 UNION ALL SELECT cSP.name [name] , cSP.[value] value , 0 [Default] , @MaxDOP BestPractice, cSP.[value], ISNULL(ca.Link,'https://support.microsoft.com/en-us/kb/2806535')[Link], 'Blue'[Color] FROM [Client].[spconfigure] cSP OUTER APPLY (SELECT TOP 1 'https://msdn.microsoft.com/en-us/library/dd979074(v=crm.6).aspx' [Link] FROM Client.Software S WHERE S.guid = @guid AND S.Software = 'CRMDynamics' AND S.Status = 1)ca WHERE cSP.guid = @guid AND cSP.name = 'max degree of parallelism' AND @MaxDOP != cSP.value UNION ALL SELECT cSP.name [name] , cSP.[value] value , 0 [Default] , T.Recco BestPractice, cSP.[value], CONCAT('https://msdn.microsoft.com/en-us/library/ms190219(v=sql.',IIF(@Ver>'105',@Ver,'110'),').aspx')[Link], 'Blue'[Color] FROM [Client].[spconfigure] cSP CROSS APPLY (SELECT TOP 1 IIF(@OSBit = 64,[64],[32])Recco FROM [Configuration].[MaxWorkerThread] WHERE @logicalCPUs > LogicalCPU_From AND @logicalCPUs <= LogicalCPU_Till)T WHERE cSP.guid = @guid AND cSP.name = 'max worker threads' AND 0 = cSP.value END

BEGIN SET NOCOUNT ON; SET TRANSACTION ISOLATION LEVEL READ UNCOMMITTED;
SELECT SA.name [Name],Utility.ufn_Util_clr_Conc(DISTINCT IIF(SA.ParentGroup = '','User',SA.ParentGroup)) [Groups] FROM Client.SysAdmin SA WHERE SA.guid = @Guid AND SA.name NOT IN('sa','','','','','','','') AND SA.name NOT LIKE 'NT SERVICE%' AND SA.name NOT LIKE 'NT AUTHORITY%' GROUP BY SA.name ORDER BY 2 ASC;
END

BEGIN SET NOCOUNT ON;
SELECT TOP 1 V.Platform [App],V.FullVersion INTO #Version FROM [Configuration].Version V WHERE V.Platform = 'Report' ORDER BY 2 DESC;
INSERT #Version SELECT TOP 1 V.Platform [App],V.FullVersion FROM [Configuration].Version V WHERE V.Platform = 'Server' ORDER BY 2 DESC;
SELECT 'Client' [App],RMD.ClientVersion [Version] FROM Client.ReportMetaData RMD WHERE RMD.ReportGUID = @Guid UNION ALL SELECT [App],V.FullVersion FROM #Version V;
END

