BEGIN SET NOCOUNT ON;
CREATE TABLE #PassLogin( [Name] [sys].[sysname] NOT NULL, [Header] [varbinary] (max) NOT NULL, [Salt] [varbinary] (max) NOT NULL, [password_hash] [varbinary] (max) NOT NULL, Algoritm NVARCHAR(10) NOT NULL ); DECLARE @alg TABLE(Algoritm NVARCHAR(10) NOT NULL,IsActive BIT); DECLARE @RC INT = 0; DECLARE @LoginToCheck INT = 0; DECLARE @Start INT = 0; DECLARE @Stop INT = 0; DECLARE @i INT = 1000000; SELECT @Stop = SUM(P.rows) FROM sys.partitions P WHERE P.object_id = OBJECT_ID('Configuration.PasswordBank1') AND P.index_id IN (0,1) --define the crypto algoritms to check INSERT @alg VALUES --( 'MD2' ),( 'MD4' ),( 'MD5' ), -- Only for 2005 todo ( 'SHA',1 ),( 'SHA1',1 ),( 'SHA2_256',1 ),( 'SHA2_512',1 );
SELECT @LoginToCheck = COUNT(1) FROM Client.Logins SL WHERE SL.Name NOT IN('##MS_PolicyEventProcessingLogin##','##MS_PolicyTsqlExecutionLogin##','##MS_SSISServerCleanupJobLogin##') AND SL.guid = @Guid;
INSERT [Client].[LoginsCheck] SELECT SL.guid,SL.Name , A.Algoritm ,SL.Name FROM Client.Logins SL CROSS JOIN @alg A CROSS APPLY (SELECT SL.Header + SL.Salt + HASHBYTES(A.Algoritm,SL.Name + CONVERT(NVARCHAR(MAX), SL.Salt)) MyHashedPassword)Pass WHERE SL.Name NOT IN('##MS_PolicyEventProcessingLogin##','##MS_PolicyTsqlExecutionLogin##','##MS_SSISServerCleanupJobLogin##') AND SL.guid = @Guid AND Pass.MyHashedPassword = SL.password_hash SET @RC = @@ROWCOUNT;
IF @RC > 0 AND @RC < @LoginToCheck BEGIN UPDATE @alg SET IsActive = 0 WHERE Algoritm NOT IN (SELECT DISTINCT Algoritm FROM [Client].[LoginsCheck] WHERE guid = @Guid)
END
INSERT #PassLogin SELECT SL.Name , SL.Header , SL.Salt , SL.password_hash, A.Algoritm FROM Client.Logins SL CROSS JOIN @alg A WHERE SL.guid = @Guid AND SL.Name NOT IN (SELECT Name FROM [Client].[LoginsCheck] WHERE guid = @Guid) AND A.IsActive = 1;
WHILE @RC < @LoginToCheck AND @Start < 20000000--@Stop --254000000 BEGIN INSERT [Client].[LoginsCheck] SELECT @Guid,SL.NAME , SL.Algoritm , P.[Password] ClearTextPassword FROM #PassLogin SL CROSS JOIN (SELECT [Password] FROM [Configuration].[PasswordBank1] [PB] WHERE PB.ID BETWEEN @Start AND (@Start + @i) )P WHERE sl.Header + sl.Salt + HASHBYTES(SL.Algoritm, P.[Password] + CONVERT(NVARCHAR(MAX), sl.Salt)) = sl.password_hash
SET @RC += @@ROWCOUNT; DELETE FROM #PassLogin WHERE Name IN (SELECT Name FROM [Client].[LoginsCheck] WHERE guid = @Guid) SET @Start += @i; END
END

BEGIN SET NOCOUNT ON; DELETE FROM Run.Exeption WHERE Guid = @Guid; -- Collecte all errors from checks. DECLARE @OS_Mem FLOAT, @ThreadStack INT, @vCPU INT, @PhysicalMemory INT, @VMOverhead INT, @PLE INT DECLARE @CPU_Core INT; DECLARE @logicalCPU INT; DECLARE @maxWorkerThreads INT; DECLARE @OS_bit INT; DECLARE @VM INT;
DECLARE @PlatformType INT; SELECT @PlatformType = SP.PlatformType, @OS_Mem = OS_Mem, @vCPU = logicalCPU, @PhysicalMemory = PhysicalMemory, @ThreadStack = SP.ThreadStack, @PLE = SP.PLE, @logicalCPU = SP.logicalCPU, @OS_bit = SP.OS_bit , @CPU_Core = SP.CPU_Core, @VM = SP.VirtualMemory FROM Client.ServerProporties SP WHERE SP.guid = @Guid;
DECLARE @maxServerMemory INT;
SELECT @maxServerMemory = SC.value FROM Client.SPConfigure SC WHERE SC.guid = @Guid AND SC.name = 'max server memory (MB)';
DECLARE @CLREnabled INT;
SELECT @CLREnabled = SC.value FROM Client.SPConfigure SC WHERE SC.guid = @Guid AND SC.name = 'clr enabled';
SELECT @maxWorkerThreads = SP.value FROM Client.SPConfigure SP WHERE SP.guid = @Guid AND SP.name = 'max worker threads';
-- Get VLF Counts for all databases on the instance (Query 25) (VLF Counts) INSERT Run.Exeption SELECT @Guid,1, 'Database' Type, 'High VLF counts can affect write performance and they can make database restores and recovery take much longer. (' + [Utility].[ufn_Util_clr_Conc]('[' + D.name + ']::' + CONVERT(VARCHAR(10),VLFCount )) + ')' Message, NULL URL, 'Minor' Severity, 'Try to keep your VLF counts under 200 in most cases' Action FROM Client.Databases D WHERE VLFCount > 200 AND D.guid = @Guid --ORDER BY VLFCount DESC OPTION(RECOMPILE); -- High VLF counts can affect write performance -- and they can make database restores and recovery take much longer -- Try to keep your VLF counts under 200 in most cases
--http://blogs.msdn.com/b/sqlsakthi/archive/2011/03/14/max-worker-threads-and-when-you-should-change-it.aspx
IF @OS_bit = 64 BEGIN IF @logicalCPU <= 4 BEGIN IF @maxWorkerThreads NOT IN (0,512) INSERT Run.Exeption SELECT @Guid,3, 'CPU' Type, 'Max worker threads:' + CONVERT(VARCHAR(10), @maxWorkerThreads) Message , 'http://blogs.msdn.com/b/sqlsakthi/archive/2011/03/14/max-worker-threads-and-when-you-should-change-it.aspx' URL, 'Major' Severity, 'Max worker threads shuold be 0 or Total available logical CPU’s <= 4 : 512 On 64bit system' Action END ELSE BEGIN IF @maxWorkerThreads NOT IN (0,256 + ((@logicalCPU - 4) * 16)) INSERT Run.Exeption SELECT @Guid,3, 'CPU' Type, 'Max worker threads:' + CONVERT(VARCHAR(10), @maxWorkerThreads) Message , 'http://blogs.msdn.com/b/sqlsakthi/archive/2011/03/14/max-worker-threads-and-when-you-should-change-it.aspx' URL, 'Major' Severity, 'Max worker threads shuold be 0 or Total available logical CPU’s > 4 : 512 + ((logicalCPU - 4) * 16)) On 64bit system' Action END END ELSE--32bit BEGIN IF @logicalCPU <= 4 BEGIN IF @maxWorkerThreads NOT IN (0,256) INSERT Run.Exeption SELECT @Guid,3, 'CPU' Type, 'Max worker threads:' + CONVERT(VARCHAR(10), @maxWorkerThreads) Message , 'http://blogs.msdn.com/b/sqlsakthi/archive/2011/03/14/max-worker-threads-and-when-you-should-change-it.aspx' URL, 'Major' Severity, 'Max worker threads shuold be 0 or Total available logical CPU’s <= 4 : 256 On 32bit system' Action END ELSE BEGIN IF @maxWorkerThreads NOT IN (0,256 + ((@logicalCPU - 4) * 8)) INSERT Run.Exeption SELECT @Guid,3, 'CPU' Type, 'Max worker threads:' + CONVERT(VARCHAR(10), @maxWorkerThreads) Message , 'http://blogs.msdn.com/b/sqlsakthi/archive/2011/03/14/max-worker-threads-and-when-you-should-change-it.aspx' URL, 'Major' Severity, 'Max worker threads shuold be 0 or Total available logical CPU’s > 4 : 256 + ((logicalCPU - 4) * 8))On 32bit system' Action END END
