INSERT Run.Exeption SELECT @Guid,2, 'CPU' Type, 'CPU resources ratio of the physical cores is 1:' + CONVERT(VARCHAR(10), SP.CPU_Core) Message , 'http://www.vmware.com/files/pdf/solutions/SQL_Server_on_VMware-Best_Practices_Guide.pdf' URL, 'Major' Severity, 'Provide CPU resources by maintaining a 1:1 ratio of the physical cores' Action FROM Client.ServerProporties SP WHERE sp.hyperthread_ratio != 1 AND SP.CPU_Core > 1 AND SP.guid = @Guid;
