BEGIN
    DECLARE @str VARCHAR(MAX)
    DECLARE @len INT
    DECLARE @pos INT

    SET @str = '0x';
    SET @len = DATALENGTH(@pbinin) + 1;
    SET @pos = 1;

    IF ( @pbinin IS NULL )
        RETURN NULL;

    IF ( @len = 0 )
        RETURN '0x0';

    WHILE ( @pos < @len )
    BEGIN
        DECLARE @offset INT;
        DECLARE @sub VARCHAR(2048);

		-- Thanks to Robert for bug reporting!
        SET @offset = @len - @pos
        IF @offset > 1024
            SET @offset = 1024;

        SELECT  @sub = master.sys.fn_varbintohexsubstring(0, @pbinin,@pos, @offset);
        SET @str = @str + @sub;
        SET @pos = @pos + @offset;
    END

    RETURN @str;

END

