    BEGIN
--Declare Variables
        DECLARE @Index INT ,
            @ResultString VARCHAR(200);--result string size should equal to the @string variable size
--Initialize the variables
        SET @Index = 1;
        SET @ResultString = '';
--Run the Loop until END of the string

        WHILE ( @Index < LEN(@string) + 1 )
            BEGIN
                IF ( @Index = 1 )--first letter of the string
                    BEGIN
--make the first letter capital
                        SET @ResultString = @ResultString
                            + UPPER(SUBSTRING(@string, @Index, 1));
                        SET @Index = @Index + 1;--increase the index
                    END;

-- IF the previous character is space or '-' or next character is '-'

                ELSE
                    IF ( ( SUBSTRING(@string, @Index - 1, 1) = ' '
                           OR SUBSTRING(@string, @Index - 1, 1) = '-'
                           OR SUBSTRING(@string, @Index - 1, 1) = '_'
                           OR SUBSTRING(@string, @Index + 1, 1) = '-'
                         )
                         AND @Index + 1 <> LEN(@string)
                       )
                        BEGIN
--make the letter capital
                            SET @ResultString = @ResultString
                                + UPPER(SUBSTRING(@string, @Index, 1));
                            SET @Index = @Index + 1;--increase the index
                        END;
                    ELSE-- all others
                        BEGIN
-- make the letter simple
                            SET @ResultString = @ResultString
                                + LOWER(SUBSTRING(@string, @Index, 1));
                            SET @Index = @Index + 1;--incerase the index
                        END;
            END;--END of the loop

        IF ( @@ERROR <> 0 )-- any error occur return the sEND string
            BEGIN
                SET @ResultString = @string;
            END;
-- IF no error found return the new string
        RETURN @ResultString;
    END;

begin

    declare @seconds decimal(18,3), @minutes int, @hours int;

    if(@unit = 'hour' or @unit = 'hh' )
        set @seconds = @time * 60 * 60;
    else if(@unit = 'minute' or @unit = 'mi' or @unit = 'n')
        set @seconds = @time * 60;
    else if(@unit = 'second' or @unit = 'ss' or @unit = 's')
        set @seconds = @time;
    else set @seconds = 0; -- unknown time units

    set @hours = convert(int, @seconds /60 / 60);
    set @minutes = convert(int, (@seconds / 60) - (@hours * 60 ));
    set @seconds = @seconds % 60;

    return 
        convert(varchar(9), convert(int, @hours)) + ':' +
        right('00' + convert(varchar(2), convert(int, @minutes)), 2) + ':' +
        right('00' + convert(varchar(6), @seconds), 6)

end

create function Utility.[ufn_ConvertTimeToHHMMSS]
(
    @time decimal(28,3), 
    @unit varchar(20)
)
returns varchar(20)
as

BEGIN DECLARE @Value SQL_VARIANT
SELECT @Value = Value FROM Configuration.Product_Configuration WHERE Name = @Name;
RETURN @Value; END

BEGIN DECLARE @Major VARCHAR(32); DECLARE @Minor VARCHAR(32); DECLARE @VersionBuild VARCHAR(32); DECLARE @Revision VARCHAR(32);
SELECT @Major = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 4), @Minor = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 3), @VersionBuild = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 2), @Revision = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 1) INSERT @RecommandedSP SELECT SSB.Build,SSB.Description,SSB.ReleaseDate, CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END [ShortName] FROM Configuration.SQLServerBuild SSB INNER JOIN ( SELECT TOP 1 * FROM Configuration.SQLServerBuild SSB WHERE @Major = SSB.Major AND CONVERT(INT,@Minor) = CONVERT(INT,SSB.Minor) AND @VersionBuild = SSB.VersionBuild AND @Revision = ISNULL(SSB.Revision,@Revision) )T ON T.Major = SSB.Major AND t.Minor = SSB.Minor OUTER APPLY (SELECT TOP 1 T.Build , T.Description , T.ReleaseDate , T.ShortName , T.Major,T.Minor,T.VersionBuild,T.Revision FROM ( SELECT SSB.Build,SSB.Description,SSB.ReleaseDate, CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END [ShortName], ROW_NUMBER() OVER (PARTITION BY CASE WHEN ssb.Description LIKE '%Cumulative update%' THEN 'CU' WHEN ssb.Description LIKE '%security update%' THEN 'SU' WHEN ssb.Description LIKE '%FIX%' THEN 'FIX' WHEN ssb.Description LIKE '%Service Pack%' OR ssb.Description LIKE '%GDR%' THEN 'SP' WHEN ssb.Description LIKE '%TLS%' THEN 'TLS' WHEN ssb.Description LIKE '%RTM%' THEN 'RTM' WHEN ssb.Description LIKE '%CTP%' THEN 'CTP' ELSE NULL END ORDER BY SSB.Build DESC) RN,SSB.Major,SSB.Minor,SSB.VersionBuild,SSB.Revision FROM Configuration.SQLServerBuild SSB WHERE @Major = SSB.Major AND CONVERT(INT,@Minor) = CONVERT(INT,SSB.Minor) AND SSB.Build > @ProductVersion)T WHERE T.RN = 1 AND T.ShortName = 'SP')SP WHERE T.Build < SSB.Build AND ISNULL(SP.Build,T.Build) <= SSB.Build AND SSB.Build NOT IN ('11.00.9120','11.00.9000') ORDER BY SSB.Build DESC;
RETURN; END

BEGIN --Declare Variables DECLARE @Ver NVARCHAR(128),@ResultString VARCHAR(1000); SELECT @Ver = ISNULL(@ProductVersion,Utility.ufn_Util_clr_RegexReplace(@Version,'([\W\w]*)\- ([\d]+\.[\d]+\.[\d]+\.[\d]+)[\W\w]*','$2',0))
IF @Edition LIKE '%Enterprise%' AND @Edition LIKE '%Core-based%' SET @Edition = 'Enterprise Core' ELSE IF @Edition LIKE '%Enterprise%' SET @Edition = 'Enterprise' ELSE IF @Edition LIKE '%Web%' SET @Edition = 'Web' ELSE IF @Edition LIKE '%Express%' SET @Edition = 'Express' ELSE IF @Edition LIKE '%Developer%' SET @Edition = 'Developer' ELSE IF @Edition LIKE '%Business Intelligence%' SET @Edition = 'Business Intelligence' ELSE IF @Edition LIKE '%Standard%' SET @Edition = 'Standard'
SELECT @ResultString = 'Microsoft SQL Server ' + CASE WHEN b.Major = 10 THEN CASE WHEN PARSENAME(CONVERT(VARCHAR(32), @Ver), 3) = '50' THEN '2008 R2' ELSE '2008' END ELSE CONVERT(VARCHAR(8),b.Year) END +' ' + @Edition FROM [Configuration].[SQLServerMajorBuild] b WHERE PARSENAME(CONVERT(VARCHAR(32), @Ver), 4) = CONVERT(NCHAR(4),b.Major) RETURN @ResultString; END;

