AS

CREATE FUNCTION [Utility].[ufn_GetSQLServerServicePack] (@Version [nvarchar](max),
@ProductLevel  [nvarchar](max),
@ProductVersion [nvarchar](max))
RETURNS VARCHAR(1000)
AS

BEGIN DECLARE @Edition NVARCHAR(MAX) ; DECLARE @ServicePack NVARCHAR(MAX) ; DECLARE @Description NVARCHAR(MAX) ; DECLARE @Major VARCHAR(32); DECLARE @Minor VARCHAR(32); DECLARE @VersionBuild VARCHAR(32);
SELECT @Major = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 4), @Minor = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 3), @VersionBuild = PARSENAME(CONVERT(VARCHAR(32), @ProductVersion), 2)
SELECT @Description = SSB.Description FROM Configuration.SQLServerBuild SSB WHERE @Major = SSB.Major AND CONVERT(INT,@Minor) = CONVERT(INT,SSB.Minor) AND CONVERT(INT,@VersionBuild) = CONVERT(INT,SSB.VersionBuild)
IF @Ver LIKE '%Enterprise%' OR @Ver LIKE '%Core-based%' SET @Edition = 'Enterprise Core' ELSE IF @Ver LIKE '%Enterprise%' SET @Edition = 'Enterprise' ELSE IF @Ver LIKE '%Web%' SET @Edition = 'Web' ELSE IF @Ver LIKE '%Express%' SET @Edition = 'Express' ELSE IF @Ver LIKE '%Developer%' SET @Edition = 'Developer' ELSE IF @Ver LIKE '%Business Intelligence%' SET @Edition = 'Business Intelligence' ELSE IF @Ver LIKE '%Standard%' SET @Edition = 'Standard'
SELECT @Edition = 'Microsoft SQL Server ' + CASE WHEN b.Major = 10 THEN CASE WHEN @Minor = '50' THEN '2008 R2' ELSE '2008' END ELSE CONVERT(VARCHAR(8),b.Year) END +' ' + @Edition FROM [Configuration].[SQLServerMajorBuild] b WHERE @Major = CONVERT(NCHAR(4),b.Major);
IF @Description LIKE '%Service Pack 1%' OR @Description LIKE '% SP1 %' SET @ServicePack = 'Service Pack 1' ELSE IF @Description LIKE '%Service Pack 2%' OR @Description LIKE '% SP2 %' SET @ServicePack = 'Service Pack 2' ELSE IF @Description LIKE '%Service Pack 3%' OR @Description LIKE '% SP3 %' SET @ServicePack = 'Service Pack 3' ELSE IF @Description LIKE '%Service Pack 4%' OR @Description LIKE '% SP4 %' SET @ServicePack = 'Service Pack 4' ELSE IF @Description LIKE '%Service Pack 5%' OR @Description LIKE '% SP5 %' SET @ServicePack = 'Service Pack 5' ELSE IF @Description LIKE '%Service Pack 6%' OR @Description LIKE '% SP6 %' SET @ServicePack = 'Service Pack 6' ELSE IF @Description LIKE '%Service Pack 7%' OR @Description LIKE '% SP7 %' SET @ServicePack = 'Service Pack 7' ELSE IF @Description LIKE '%Service Pack 8%' OR @Description LIKE '% SP8 %' SET @ServicePack = 'Service Pack 8' ELSE IF @Description LIKE '%Service Pack 9%' OR @Description LIKE '% SP9 %' SET @ServicePack = 'Service Pack 9';
SELECT @ServicePack = 'Microsoft SQL Server ' + CASE WHEN b.Major = 10 THEN CASE WHEN @Minor = '50' THEN '2008 R2' ELSE '2008' END ELSE CONVERT(VARCHAR(8),b.Year) END +' ' + @ServicePack FROM [Configuration].[SQLServerMajorBuild] b WHERE @Major = CONVERT(NCHAR(4),b.Major);
INSERT @SupportEndDate SELECT TOP 1 TRY_CONVERT(NVARCHAR(35),MAX(T.SupportEndDate))[SupportEndDate] FROM (
SELECT TOP 1 [CompatibilityLevel] ,[ProductsReleased] ,[StartDate] ,CASE WHEN [MainstreamSupportEndDate] > [ExtendedSupportEndDate] THEN [MainstreamSupportEndDate] ELSE [ExtendedSupportEndDate] END [SupportEndDate] FROM [Configuration].[LifeCycleSupport] WHERE @Major + IIF(@Minor = '50','5','0') = [CompatibilityLevel] AND @Edition like ProductsReleased + '%' UNION ALL SELECT TOP 1 [CompatibilityLevel] ,[ProductsReleased] ,[StartDate] ,iif(ExtendedSupportEndDate > [ServicePackSupportEndDate],ExtendedSupportEndDate,[ServicePackSupportEndDate]) [SupportEndDate] FROM [Configuration].[LifeCycleSupport] WHERE @Major + IIF(@Minor = '50','5','0') = [CompatibilityLevel] AND @ServicePack = ProductsReleased )T HAVING MAX(T.SupportEndDate) IS NOT NULL;
RETURN; END

BEGIN
	SET NOCOUNT ON;

    DECLARE @cmd VARCHAR(MAX);
    DECLARE @ClientName NVARCHAR(1000);
	DECLARE @ClientID INT;
    DECLARE @getClientName CURSOR ;
	DECLARE @DirectoryCreate NVARCHAR(1000);	
	DECLARE @DirectoryCreate2 NVARCHAR(1000);
	DECLARE @ReportGuid UNIQUEIDENTIFIER;
	DECLARE @ExportPath NVARCHAR(MAX) = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('ExportPath'));
	DECLARE @sFileNamePath nvarchar(max);
	DECLARE @ExportReportName NVARCHAR(512);

    EXECUTE [Utility].[DirectoryCreate] @ExportPath;
	----
	DECLARE @FullName NVARCHAR(MAX);
	DECLARE @eMail NVARCHAR(MAX);


	SET @getClientName = CURSOR LOCAL FAST_FORWARD FOR
	SELECT	RMD.ClientName,CONCAT(@ExportPath,'\',LC.eMail,'\Mail'),ISNULL(RMD.ClientID,1),RMD.ReportGUID,RMD.ExportReportName
	FROM	Client.ReportMetaData RMD 
			INNER JOIN [SiteReviewUser].[dbo].[Login] L ON ISNULL(RMD.ClientId,1) = L.ID
			INNER JOIN [SiteReviewUser].[dbo].LoginCheck LC  ON LC.LoginID = L.ID
	WHERE	RMD.IsExported = 1 
			AND RMD.HaveExportError = 0
			AND HaveSent = 0
			AND SentError IS NULL;
    OPEN @getClientName;
    FETCH NEXT FROM @getClientName INTO @ClientName,@DirectoryCreate,@ClientID,@ReportGuid,@ExportReportName;

    WHILE @@FETCH_STATUS = 0
    BEGIN
        BEGIN TRY
			--PRINT @DirectoryCreate
			EXECUTE [Utility].[DirectoryCreate] @DirectoryCreate;
			--Delete files from @DirectoryCreate
            SET @cmd = 'xp_cmdshell ''del "' + @DirectoryCreate + '\" /q'',no_output';--
			--PRINT @cmd;
            EXEC (@cmd);
			SET @sFileNamePath = REPLACE(@DirectoryCreate,'\Mail','') + '\*.PDF'
            SET @cmd = 'xp_cmdshell ''cd.. && "C:\Program Files\WinRAR\Rar.exe" a -ep "' + @DirectoryCreate
                + '\Reports.rar" "' + @sFileNamePath + '"'',no_output';--
			--PRINT @cmd;
            EXEC (@cmd);

			UPDATE  Client.ReportMetaData
            SET     HaveSent = 1
            WHERE   IsExported = 1
					AND HaveSent = 0
					AND HaveExportError = 0
                    AND ClientID = @ClientID;

			SET @sFileNamePath = REPLACE(@sFileNamePath,'*.PDF','') + @ExportReportName;

			EXECUTE [Utility].[FileDelete] @sFileNamePath;

        END TRY
        BEGIN CATCH
            DECLARE @ErMessage NVARCHAR(2048) ,
                @ErSeverity INT ,
                @ErState INT;
            SELECT  @ErMessage = ERROR_MESSAGE() ,
                    @ErSeverity = ERROR_SEVERITY() ,
                    @ErState = ERROR_STATE();

            RAISERROR (@ErMessage,
				@ErSeverity,
				@ErState );
            UPDATE  Client.ReportMetaData
            SET     SentError = CONCAT('Create RAR file - ',@ErMessage)
            WHERE   IsExported = 1
					AND HaveSent = 0
					AND HaveExportError = 0
                    AND ReportGUID = @ReportGuid;
        END CATCH;
        FETCH NEXT FROM @getClientName INTO @ClientName,@DirectoryCreate,@ClientID,@ReportGuid,@ExportReportName;
    END;

    CLOSE @getClientName;
    DEALLOCATE @getClientName;

END

BEGIN
    SET NOCOUNT ON;

    DECLARE @cmd VARCHAR(MAX);
    DECLARE @ClientName VARCHAR(255);
    DECLARE @ClientID INT;
	DECLARE @ReportGUID UNIQUEIDENTIFIER;
	DECLARE @emailBCC VARCHAR(MAX) = '';
    DECLARE @file_attachment VARCHAR(1000);	

	DECLARE @msg NVARCHAR(MAX);
	DECLARE @error NVARCHAR(MAX);
    DECLARE @MainSubject VARCHAR(1000) = 'Site Review - SQL Server Health Check';
    DECLARE @errormsg VARCHAR(MAX);
    DECLARE @bodymsg VARCHAR(MAX);
    DECLARE @getClientName CURSOR;
    DECLARE @fileexistsresult INT;	

	DECLARE @ExportPath NVARCHAR(MAX) = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('ExportPath'));
	DECLARE @MailProfile sysname = (SELECT TOP 1 name FROM msdb.dbo.sysmail_profile);
	DECLARE @eMail NVARCHAR(MAX);

	DECLARE @SlackToken NVARCHAR(MAX);
	SELECT	@SlackToken = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('SlackToken')),
			@emailBCC = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('emailBCC'));

    SET @getClientName = CURSOR LOCAL FORWARD_ONLY FOR
	SELECT	[Utility].[ufn_CapitalizeFirstLetter](ISNULL(L.FullName,N'General Client')),ISNULL(RMD.ClientID,1),RMD.ReportGUID ,LC.eMail
	FROM	Client.ReportMetaData RMD 
			INNER JOIN [SiteReviewUser].[dbo].[Login] L ON ISNULL(RMD.ClientId,1) = L.ID
			INNER JOIN [SiteReviewUser].[dbo].LoginCheck LC  ON LC.LoginID = L.ID
	WHERE	RMD.HaveSent = 0 
			AND RMD.HaveExportError = 0;

    OPEN @getClientName;
    FETCH NEXT FROM @getClientName INTO @ClientName,@ClientID,@ReportGUID,@eMail;

    WHILE @@FETCH_STATUS = 0
        BEGIN


			IF @eMail IS NULL OR @eMail = ''
			BEGIN
				SET @eMail =@emailBCC
			END

			SELECT @ClientName = [Utility].[ufn_CapitalizeFirstLetter](@ClientName);
			SELECT @bodymsg = CONCAT('Dear ',@ClientName,',

Thank you for using SQL Server ');


            SET @file_attachment = CONCAT(@ExportPath,'\',@eMail,'\Mail\Reports.rar');
            EXEC master.dbo.xp_fileexist @file_attachment,
                @fileexistsresult OUTPUT;
            IF @fileexistsresult = 1 AND @eMail IS NOT NULL
            BEGIN
				BEGIN TRY
					EXEC msdb.dbo.sp_send_dbmail 
						@profile_name = @MailProfile,
						@recipients = @eMail, 
						@blind_copy_recipients = @emailBCC,
						@subject = @MainSubject,
						@body = @bodymsg, 
						@file_attachments = @file_attachment,
						@body_format = 'HTML',
						@exclude_query_output = 1;

					UPDATE  [Client].[ReportMetaData]
					SET     HaveSent = 1
					WHERE   ReportGUID = @ReportGUID;

					SELECT @msg = CONCAT(@@SERVERNAME,':: ',@MainSubject,' :: ',@ClientName,' - ',' PDF has been sent.')
				END TRY
				BEGIN CATCH
					SET @error = ERROR_MESSAGE();
					UPDATE	[Client].[ReportMetaData] 
					SET		HaveExportError = 1,ExportError = @error
					WHERE	IsExported = 0 
							AND ReportGUID = @ReportGUID
							AND HaveExportError = 0;

					SELECT @msg = CONCAT(@@SERVERNAME,':: ',@MainSubject,' :: ',@ClientName,' - Error:: ',@error)
				END CATCH


            END;
            ELSE
            BEGIN
                SET @errormsg = 'Client Name: ' + @ClientName;
                SET @errormsg = CASE WHEN @fileexistsresult = 0
                                        THEN @errormsg
                                            + '; Error: Missing file attachment'
                                        WHEN @eMail IS NULL
                                        THEN @errormsg
                                            + '; Error: Missing Email Recipients'
                                END;
				UPDATE	[Client].[ReportMetaData] 
				SET		HaveExportError = 1,ExportError = @errormsg
				WHERE	HaveSent = 0 
						AND ReportGUID = @ReportGUID
						AND HaveExportError = 0;

				SELECT @msg = CONCAT(@@SERVERNAME,':: ',@MainSubject,' :: ',@errormsg)
                --RAISERROR(@errormsg,16,1);
            END;
			SELECT Ok,
				Channel,
				TimeStamp,
				Error
			FROM [Utility].SlackChatPostMessage(
				@SlackToken,
				'#sitereview',
				@msg,
				@ClientName,
				NULL
			)
            FETCH NEXT FROM @getClientName INTO @ClientName,@ClientID,@ReportGUID,@eMail;
        END;

    CLOSE @getClientName;
    DEALLOCATE @getClientName;

END

BEGIN
	SET NOCOUNT ON;

	--Constant 4 This Environment
	DECLARE @ServicePathUrl NVARCHAR(MAX) = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('ServicePathUrl'));
	DECLARE @reportPath NVARCHAR(MAX) = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('ReportPath'));
	DECLARE @ExportPath NVARCHAR(MAX) = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('ExportPath'));
	DECLARE @ExportPathReport NVARCHAR(MAX);

	DECLARE @ExportReportName NVARCHAR(MAX);
	DECLARE @FullName NVARCHAR(MAX);
	DECLARE @eMail NVARCHAR(MAX);
	DECLARE @ReportGUID VARCHAR(50);


    DECLARE @ClientID INT;
    DECLARE @ClientName VARCHAR(1000);
    DECLARE @cmd VARCHAR(MAX);

	DECLARE cuExport CURSOR FAST_FORWARD READ_ONLY FOR 
	SELECT	FORMAT(RMD.RunDate,'yyyyMMdd') + '-' + REPLACE(RMD.ServerName,'\','@') ,RMD.ReportGUID,ISNULL(RMD.ClientID,1),L.FullName, LC.eMail
	FROM	Client.ReportMetaData RMD 
			INNER JOIN [SiteReviewUser].[dbo].[Login] L ON ISNULL(RMD.ClientId,1) = L.ID
			INNER JOIN [SiteReviewUser].[dbo].LoginCheck LC  ON LC.LoginID = L.ID
	WHERE	RMD.IsExported = 0 
			AND RMD.HaveExportError = 0;

	OPEN cuExport

	FETCH NEXT FROM cuExport INTO @ExportReportName,@ReportGUID,@ClientID,@FullName,@eMail;

	WHILE @@FETCH_STATUS = 0
	BEGIN
		BEGIN TRY

			SET @ExportPathReport = CONCAT(@ExportPath,'\',@eMail);
			EXECUTE [Utility].[DirectoryCreate] @ExportPathReport;
			--Delete files from @DirectoryCreate
            SET @cmd = 'xp_cmdshell ''del "' + @ExportPathReport + '\" /q'',no_output';--
			--PRINT @cmd;
            EXEC (@cmd);

			EXECUTE Utility.[usp_clr_ExportPDFReport] 
					   @ServicePathUrl
					  ,@reportPath
					  ,@ExportPathReport
					  ,@ExportReportName
					  ,@ReportGUID;

			UPDATE	Client.ReportMetaData
			SET		IsExported =1,
					ExportReportName = @ExportReportName
			WHERE	ReportGUID = @ReportGUID
					AND IsExported = 0;
		END TRY
		BEGIN CATCH
			THROW;
			UPDATE	Client.ReportMetaData
			SET		IsExported = 0, HaveExportError = 1,ExportError = 'Error While creating PDF'
			WHERE	ReportGUID = @ReportGUID
		END CATCH
		FETCH NEXT FROM cuExport INTO @ExportReportName,@ReportGUID,@ClientID,@FullName,@eMail;
	END

	CLOSE cuExport;
	DEALLOCATE cuExport;

	EXEC Utility.[usp_Auto_AddReportsToRar];

END

-- =============================================
-- Author:		Sharon
-- Create date: 21/10/2014
-- Update date: 29/10/2014 Set output parameter
--				02/11/2014 Sharon Partition AND P.index_id IN (0,1)
--				03/11/2014 Sharon Update
--				13/11/2014 Sharon @ColumnFillter @IgnoreColumnFillter
--				20/11/2014 Sharon Insert @ColumnFillter @IgnoreColumnFillter
--				01/12/2014 Sharon Path not Exists
--				29/12/2014 Sharon IF @Path IS NOT NULL AND @IsDebug = 0
--				02/07/2015 Sharon IF EXISTS
--				20/08/2015 Niro @Filename added condition if table is datadic_filed
--				14/10/2015 Sharon Fix @Filename with replace illigle chars
--				22/11/2015 Niro Changing the file name format
-- Description:	Create Script .sql file in path for insert\update\dalete
-- =============================================
CREATE PROCEDURE Utility.[usp_CreateScriptFile]
(
	@table_name SYSNAME,
	@IsTruncate BIT = 1,
	@IsDebug BIT = 0,
	@Path VARCHAR(600),
	@Fillter VARCHAR(MAX),
	@Output NVARCHAR(MAX) OUTPUT,
	@Action int = 1, -- 1. Insert, 2. Update 3.All
	@IsSafeInsert BIT = 0,
	@ColumnFillter VARCHAR(MAX),
	@IgnoreColumnFillter VARCHAR(MAX),
	@Advanced BIT = 0
)
AS

BEGIN
    SET NOCOUNT ON;

	EXEC SiteReviewUser.SiteReviewUser.[GUI].[usp_GetLoginInfo]	@LoginID  ,	@FullName OUTPUT,@eMail OUTPUT;
END

BEGIN
    SET NOCOUNT ON;

	DECLARE @MaxID INT;

	INSERT	INTO Client.XMLReports
	        ( XMLData ,
	          LoadedDateTime ,
	          IsPopulated ,
	          FileName,ClientID
	        )
	EXEC [SiteReviewUser].[SiteReviewUser].[GUI].[usp_GetXMLReport] @MaxID OUTPUT;

	EXEC [SiteReviewUser].[SiteReviewUser].[GUI].[usp_DeleteXMLReport] @MaxID;
END

BEGIN SET NOCOUNT ON;
INSERT [SiteReviewUser].[SiteReviewUser].[dbo].[ClientScript](Ver,Date,Script) SELECT @Ver,GETDATE(),SM.definition FROM master.sys.sql_modules SM INNER JOIN master.sys.objects O ON O.object_id = SM.object_id WHERE o.name = 'sp_SiteReview'
END

BEGIN
    SET NOCOUNT ON;

	DECLARE @msg NVARCHAR(MAX) = '';
	DECLARE @CountP INT;
	DECLARE @CountS INT;

	SELECT	*
	INTO	#SQLServerBuild
	FROM	[CloudAzure].[DataPlatform].[Configuration].[SQLServerBuild];

	SELECT	@CountP = COUNT(1)
	FROM	#SQLServerBuild;
	SELECT	@CountS = COUNT(1)
	FROM	[Configuration].[SQLServerBuild];

	IF @CountP > @CountS
	BEGIN
		DELETE FROM [Configuration].[SQLServerBuild];
		INSERT	[Configuration].[SQLServerBuild](Build,FileVersion,Description,ReleaseDate)
		SELECT	Build,FileVersion,Description,ReleaseDate
		FROM	#SQLServerBuild;

		SET @msg += CONCAT('SQLServerBuild - ',@CountP - @CountS,' rows added.
');
	END
	-----------------------------------------------------------
	SELECT	*
	INTO	#LifeCycleSupport
	FROM	[CloudAzure].[DataPlatform].[Configuration].[LifeCycleSupport];

	SELECT	@CountP = COUNT(1)
	FROM	#LifeCycleSupport;
	SELECT	@CountS = COUNT(1)
	FROM	[Configuration].[LifeCycleSupport];

	IF @CountP > @CountS
	BEGIN
		DELETE FROM [Configuration].[LifeCycleSupport];
		INSERT	[Configuration].[LifeCycleSupport]
		        ( [CompatibilityLevel] ,
		          [ProductsReleased] ,
		          [StartDate] ,
		          [MainstreamSupportEndDate] ,
		          [ExtendedSupportEndDate] ,
		          [ServicePackSupportEndDate]
		        )
		SELECT	[CompatibilityLevel] ,
		          [ProductsReleased] ,
		          [StartDate] ,
		          [MainstreamSupportEndDate] ,
		          [ExtendedSupportEndDate] ,
		          [ServicePackSupportEndDate]
		FROM	#LifeCycleSupport;

		SET @msg += CONCAT('LifeCycleSupport - ',@CountP - @CountS,' rows added.
')
	END

	IF LEN(@msg) > 0
	BEGIN
		DECLARE @SlackToken NVARCHAR(MAX);
		SELECT	@SlackToken = CONVERT(NVARCHAR(MAX),[Utility].[ufn_GetConfiguration] ('SlackToken'));
		SELECT Ok,
			Channel,
			TimeStamp,
			Error
		FROM [Utility].SlackChatPostMessage(
			@SlackToken,
			'#sitereview',
			@msg,
			'UpdateDataPlatform',
			null
		);
	END

END

BEGIN
	SET NOCOUNT ON;
	DECLARE @cmd NVARCHAR(max);
	SELECT @cmd = N'INSERT [Configuration].[Images] SELECT	@Name,(SELECT * FROM OPENROWSET (BULK ''' + @Path + ''', SINGLE_BLOB) my);';

	EXEC sp_executesql @cmd, N'@Name sysname',@Name = @Name;

END
